        uint256,
        uint96[] memory,
        uint96[] memory,
        bytes32[] memory,
        address,
        string calldata
    ) internal returns (uint64, uint256) {
        weth.safeTransferFrom(msg.sender, address(this), amount);

        return (0, amount);
    }
}

